// Soluzione: Promises e async/await (slide seguenti)

// Promise: oggetto che rappresenta completamento futuro
const promise = new Promise((resolve, reject) => {
  setTimeout(() => {
    const success = true;
    if (success) {
      resolve('Done!');  // Promise fulfilled
    } else {
      reject('Error!');  // Promise rejected
    }
  }, 1000);
});

// Consumare Promise
promise
