    console.log(result);  // 'Done!'
    return result.toUpperCase();
  })
  .then(upper => {
    console.log(upper);  // 'DONE!'
  })
  .catch(error => {
    console.error(error);
  })
  .finally(() => {
    console.log('Always runs');
  });

// Stati Promise: pending ‚Üí fulfilled/rejected

