// Proxy: intercetta operazioni su oggetti
const user = {
  name: 'Alice',
  age: 25
};

const handler = {
  get(target, prop) {
    console.log(`Reading ${prop}`);
    return target[prop];
  },
  set(target, prop, value) {
    console.log(`Setting ${prop} to ${value}`);
    if (prop === 'age' && typeof value !== 'number') {
      throw new TypeError('Age must be a number');
    }
    target[prop] = value;
    return true;
  }
};

const proxy = new Proxy(user, handler);
console.log(proxy.name);  // Logs: Reading name, Output: Alice
proxy.age = 26;           // Logs: Setting age to 26

// Reflect: metodi che rispecchiano operazioni oggetto
