import fs from 'fs';
import fsPromises from 'fs/promises';

// ===== Callback style (old) =====
fs.readFile('file.txt', 'utf8', (err, data) => {
  if (err) throw err;
  console.log(data);
});

// ===== Synchronous (BLOCCA event loop - evitare) =====
try {
  const data = fs.readFileSync('file.txt', 'utf8');
  console.log(data);
} catch (err) {
  console.error(err);
}

// ===== Promise-based (MODERNO - preferito) =====
try {
  const data = await fsPromises.readFile('file.txt', 'utf8');
  console.log(data);
} catch (err) {
  console.error(err);
}

// Write file
await fsPromises.writeFile('output.txt', 'Hello Node.js', 'utf8');

// Append
await fsPromises.appendFile('log.txt', 'New line\n', 'utf8');

