// ‚úÖ Next.js 16 philosophy:
// - Dynamic by default (no cache)
// - 'use cache' per opt-in
// - Partial Pre-Rendering (PPR) per mix static/dynamic

// ‚ùå Pre-Next.js 16:
// - Static by default (cache implicita)
// - { cache: 'no-store' } per opt-out
// - Confusing per developers

// Next.js 16: Nuove cache APIs
import { updateTag, revalidateTag, refresh } from 'next/cache'

// updateTag() - Update cache tag
export async function updateProduct(id: string) {
  'use server'

  await db.product.update({ where: { id }, data: { ... } })

  // Update cache tag (nuovo in v16)
  updateTag(`product-${id}`)
}

// revalidateTag() - Invalidate cache tag
export async function deleteProduct(id: string) {
  'use server'

  await db.product.delete({ where: { id } })
  revalidateTag(`product-${id}`)
  revalidateTag('products') // Invalidate list
}

// refresh() - Force refresh current page
'use client'
import { useRouter } from 'next/navigation'

export function RefreshButton() {
  const router = useRouter()

  return (
    <button onClick={() => router.refresh()}>
      Refresh Data
    </button>
  )
}

