class Counter:
    # Attributo di classe (condiviso)
    count = 0

    def __init__(self, name):
        self.name = name  # Attributo istanza
        Counter.count += 1

    def get_count(self):
        return Counter.count

    @classmethod  # Metodo di classe
    def reset_count(cls):
        cls.count = 0

    @staticmethod  # Metodo statico
    def is_valid_name(name):
        return len(name) > 0

c1 = Counter("First")
c2 = Counter("Second")
print(c1.get_count())  # 2

Counter.reset_count()
print(Counter.is_valid_name(""))  # False

# Classe base
class Animal:
