class BankAccount:
    def __init__(self, balance):
        self.__balance = balance  # Attributo privato (name mangling)

    def deposit(self, amount):
        if amount > 0:
            self.__balance += amount

    def withdraw(self, amount):
        if 0 < amount <= self.__balance:
            self.__balance -= amount
            return True
        return False

    def get_balance(self):  # Getter
        return self.__balance

account = BankAccount(1000)
account.deposit(500)
print(account.get_balance())  # 1500

# print(account.__balance)  # ‚ùå AttributeError
print(account._BankAccount__balance)  # 1500 (name mangling bypass)

class Person:
    def __init__(self, name, age):
        self._name = name
        self._age = age

    @property  # Getter
    def name(self):
        return self._name

    @property
    def age(self):
        return self._age

    @age.setter  # Setter
    def age(self, value):
        if value < 0:
            raise ValueError("Age cannot be negative")
        self._age = value

